function IN3(ins){
	var in3 = "IN3|" +
	"1|" + //Set ID - Insurance Certification
	ins.cert.certNumber+"|" + //Certification Number
	ins.cert.certBy+"|" + //Certified By
	ins.cert.required+"|" + //Certification Required
	ins.cert.penalty+"|" + //Penalty
	formatDate(ins.cert.certDate)+"|" + //Certification Date/Time
	"|" + //Certification Modify Date/Time
	formatProvider(ins.cert.provider)+"|" + //Operator
	formatDate(ins.cert.certDate)+"|" + //Certification Begin Date
	"|" + //Certification End Date
	valFromList(["AP","PE"])+"^"+randomInt(1)+"|" + //Days
	"|" + //Non-Concur Code/Description
	"|" + //Non-Concur Effective Date/Time
	formatProvider(new provider())+"|" + //Physician Reviewer
	formatProvider(ins.cert.provider)+"|" + //Certification Contact
	ins.cert.provider.phone.work+"|" + //Certification Contact Phone Number
	"|" + //Appeal Reason
	"DATICA CERTIFICATION GROUP|" + //Certification Agency
	phoneNumber()+"|" + //Certification Agency Phone Number
	"IPE|" + //Pre-Certification required/Window = inpatient elective
	formatProvider(new provider())+"|" + //Case Manager
	"|" + //Second Opinion Date
	"|" + //Second Opinion Status
	"|" + //Second Opinion Documentation Received
	formatProvider(new provider())+"|\n"; //Second Opinion Physician
	return in3.toUpperCase();
}
function ACC(){
	var acc = "ACC|" + 
	formatDate(randomDate(2016,2016))+"|" + //accident date time
	"V89.2^Person injured in unspecified motor-vehicle accident, traffic^ICD-10|" + //accident code
	"HWY "+randomLetter()+randomInt(2)+"|" + //accident location
	usState()+"|" + //auto accident state
	YN()+"|" + //accident job related indicator
	"N|\n"; //accident death indicator
	return acc.toUpperCase();
}
