function RXO(med){
	var rxo = "RXO|" +
	med.id+"^"+med.name+"^NDC"+"|" + //Requested Give Code
	med.min+"|" + //Requested Give Amount - Minimum
	"|" + //Requested Give Amount - Maximum - not used
	med.units+"|" + //Requested Give Units
	med.dosageForm+"|" + //Requested Dosage Form
	"|" + //Provider's Pharmacy Instructions
	med.providerInstructions+"|" + //Provider's Administration Instructions
	hospital()+"|" + //Deliver To Location
	valFromList(["N","G","T"])+"|" + //Allow Substitutions
	"|" + //Requested Dispense Code
	med.dispenseAmount+"|" + //Requested Dispense Amount
	"|" + //Requested Dispense Units
	"|" + //Number of Refills
	"|" + //Ordering Provider's DEA Number
	"|" + //Pharmacist/Treatment Supplier's Verifier ID
	YN()+"|" + //Needs Human Review
	"D1|" + //Requested Give Per
	med.strength+"|" + //Requested Give Strength
	med.units+"|" + //Requested Give Strength Units
	med.indication+"|" + //Indication
	"|" + //Requested Give Rate Amount
	"|\n";//Requested Give Rate Units
	return rxo.toUpperCase();
}
