function person(sex){
	if (sex == null){
		sex = gender();
	}
	var intDob = randomDate(1910,1970);
	var dob = formatDate(intDob);
	var person = {
			"id":{
				"internal":randomInt(8),
				"external":randomInt(8),
				"alternate":randomInt(8)
			},
			"account":randomInt(6),
			"ssn":900000000+randomInt(8),
			"driversLicense":"S"+randomInt(15),
			"name":{
				"last":lastName(),
				"first":"",
				"initial":randomLetter(),
				"alias":alias(),
				"mothersMaiden":lastName()
			},
			"sex":sex,
			"address":{
				"street":street(),
				"state":usState(),
				"zip":zipCode()
			},
			"intDob":intDob,
			"dob":dob,
			"race":race(),
			"phone":{
				"home":phoneNumber(),
				"work":phoneNumber()
			},
			"language":language(),
			"married":maritalStatus(),
			"religion":religion(),
			"ethnicity":ethnicity(),
			"multipleBirth":YN(),
			"birthPlace":"MADISON^"+usState(),
			"birthOrder":randomInt(1)%5+1,
			"citizenship":"US",
			"pcp":"",//formatProvider(provider()),
			"student":valFromList("F","N","P",""),
			"dependent":valFromList(["M","O","U","W"]),
			"livingArrangement":valFromList(["A","F","I","R","U"]),
			"livingWill":valFromList(["Y","F","N","I","U"]),
			"organDonor":valFromList(["Y","F","I"]),
			"separateBill":YN(),
			"publicity":valFromList(["F","N","U","O"]),
			"protection":YN(),
			"ambulatoryStatus":valFromList(["A0","A1","A2","A3","A4","A5","A6","A7","A8","A9","B1","B2","B3","B4","B5","B6"]),
			"jobStatus":valFromList(["O","P","T","U"]),
			"jobTitle":valFromList(["Teacher","Painter","Software developer","Farmer","CEO","Chef"]),
			"employeeId":randomInt(7),
			"employer":{
				"name":valFromList(["ACME GROUP","VANDELAY INDUSTRIES","THE HUMAN FUND"]),
				"address":{
					"street":street(),
					"state":usState(),
					"zip":zipCode()
				},
				"startDate":randomDate(2015,2016)
			}
	};
	switch (person.sex){
	case "M": person.name.first  = maleName();
	break;
	case "F": person.name.first = femaleName();
	}
	return person;
}
function insurance(insured){
	var effDate = randomDate(2016,2017);
	var termDate = adjustYear(effDate,3,3);
	var prov = provider();
	var certDate = adjustDate(effDate,10,20);
	var ins = {
			"planId":randomLetter()+randomInt(8),
			"company":{
				"id": randomInt(8),
				"name":"DATICA INSURANCE CO",
				"address":{
					"street":street(),
					"state":usState(),
					"zip":zipCode()
				},
				"contactPerson":"",
				"contactPhone":phoneNumber()
					},
			"groupNumber":randomInt(6),
			"groupName":"INSURANCE GROUP "+randomLetter(),
			"groupEmpId":randomInt(9),
			"groupEmployer":insured.employer.name,
			"effectiveDate":effDate,
			"expirationDate":termDate,
			"authorizationInfo":"A"+randomInt(11),
			"planType":valFromList(["HMO","PPO","EPO","HDHP","MCD"]),
			"insured":insured,
			"policyNumber":randomLetter()+randomInt(9),
			"priorPlanId":randomLetter()+randomInt(8),
			"coverageType":valFromList(["H","P","B"]),
			"deductible":valFromList(["0","500","1000","2500"]),
			"cert":{
				"certNumber":randomInt(12),
				"required":YN(),
				"provider":prov,
				"penalty":valFromList(["AT","PC"]),
				"begins":certDate,
				"certDate":certDate

			}
			}
	return ins;
}
function order(){ //could use better order services for orm
	var obsDate = randomDate(2016,2016);
	var prov = provider();
	var ord = {
			"placerId":randomInt(9)+"^DATICAORD",
			"fillerId":randomInt(9)+"^DATICAORD",
			"universalServiceId":randomInt(6)+"^GEN SCREENING",
			"observationBegin":obsDate,
			"observationEnd":obsDate,
			"provider":prov
	};
	return ord;
}
function provider(){
	var prov = person();

	prov.credential = valFromList(["MD","RN","PA","CRNA","NP"])
	return prov;
	//return randomInt(5)+"^"+randomName()+"^^^"+valFromList(["MD","RN","PA","CRNA","NP"]);
}
function appointment(){
	var appointment = {
			"placer":person(),
			"filler":person(),
			"placerId":randomInt(7),
			"fillerId":randomInt(7),
			"placerGroup":randomInt(7),
			"reason":valFromList(["CHECKUP","EMERGENCY","FOLLOWUP","ROUTINE","WALKIN"]),
			"type":valFromList(["COMPLETE","NORMAL","TENTATIVE"]),
			"duration":((randomInt(1)%4)*15),
			"fillerStatusCode":valFromList(["PENDING","WAITLIST","BOOKED","STARTED","COMPLETE","CANCELLED","DC","DELETED","BLOCKED","OVERBOOK"]),
			"date":randomDate(2017,2017)
	};
	return appointment;
}
function relative(pers){
	var sex = gender();
	var relationship = null;
	if (sex== "M"){
		relationship = valFromList(["SPO","DOM","CHD","DEP","FTH","GRD","BRO","PAR","EMC","UNK","OTH"]);
	} else if (sex =="F"){
		relationship = valFromList(["SPO","DOM","CHD","DEP","MTH","GRD","SIS","PAR","EMC","UNK","OTH"]);
	}
	var relative= person(sex);
	if (relationship == "CHD"){
		relative.intDob = adjustYear(pers.intDob,18,40);
		relative.dob = formatDate(relative.intDob);
	}else if(relationship == "FTH" || relationship == "MTH" || relationship == "PAR"|| relationship == "GRD"){
		relative.intDob = adjustYear(pers.intDob,-18,-40);
		relative.dob = formatDate(relative.intDob);
	}else if (relationship == "SPO"){
		pers.married = "Y";
		relative.married = "Y";
	}
	relative.relationship = relationship;
	//mothers maiden name update
	return relative;}
