			"|" + //readmission indicator - either R or null
			randomInt(1)+"|" + //admit source - 1-9
			patient.ambulatoryStatus+"|" + //ambulatory status
			"|" + //vip indicator - leaving blank
			formatProvider(provider())+"|" + //admitting doctor
			"U|" + //patient type - can't find suggested values
			randomInt(6)+"|" + //visit number
			"A23|" + //financial class
			"R|" + //charge price indicator
			"N|" + //courtesy code
