function RXE(med){
	var rxe = "RXE|" +
	med.quantityTiming+"|" + // Quantity/Timing
	med.id+"^"+med.name+"^NDC"+"|" + // Give Code
	med.min+"|" + // Give Amount - Minimum
	"|" + // Give Amount - Maximum
	med.units+"|" + // Give Units
	med.dosageForm+"|" + // Give Dosage Form
	med.providerInstructions+"|" + // Provider's Administration Instructions
	"|" + // Deliver To Location
	valFromList(["N","G","T","0","1","2","3","4","5","6","7","8"])+"|" + // Substitution Status
	med.dispenseAmount+"|" + // Dispense Amount
	med.units+"|" + // Dispense Units
	randomInt(1)%3+"|" + // Number of Refills
	"|" + // Ordering Provider's DEA Number
	"|" + // Pharmacist/Treatment Supplier's Verifier ID
	"|" + // Prescription Number
	"|" + // Number of Refills Remaining
	"|" + // Number of Refills/Doses Dispensed
	"|" + // Date / time of most recent refill or dose dispensed
	"|" + // Total Daily Dose
	YN()+"|" + // Needs Human Review
	"|" + // Pharmacy/Treatment Supplier's Special Dispensing Instructions
	"|" + // Give Per
	"|" + // Give Rate Amount
	"|" + // Give Rate Units
	"|" + // Give Strength
	"|" + // Give Strength Units
	"|" + // Give Indication
	"|" + // Dispense Package Size
	"|" + // Dispense Package Size Unit
	"|\n"; // Dispense Package Method
	return rxe.toUpperCase();
}

