function person(sex){
	if (sex == null){
		sex = gender();
	}
	var intDob = randomDate(1910,1970);
	var dob = formatDate(intDob);
	var person = {
			"id":{
				"internal":randomInt(8),
				"external":randomInt(8),
				"alternate":randomInt(8)
			},
			"account":randomInt(6),
			"ssn":900000000+randomInt(8),
			"driversLicense":"S"+randomInt(15),
			"name":{
				"last":lastName(),
				"first":"",
				"initial":randomLetter(),
				"alias":alias(),
				"mothersMaiden":lastName()
			},
			"sex":sex,
			"address":{
				"street":street(),
				"state":usState(),
				"zip":zipCode()
			},
			"intDob":intDob,
			"dob":dob,
			"race":race(),
			"phone":{
				"home":phoneNumber(),
				"work":phoneNumber()
			},
			"language":language(),
			"married":maritalStatus(),
			"religion":religion(),
			"ethnicity":ethnicity(),
			"multipleBirth":YN(),
			"birthPlace":"MADISON^"+usState(),
			"birthOrder":randomInt(1)%5+1,
			"citizenship":"US",
			"pcp":"",//formatProvider(provider()),
			"student":valFromList("F","N","P",""),
			"dependent":valFromList(["M","O","U","W"]),
			"livingArrangement":valFromList(["A","F","I","R","U"]),
			"livingWill":valFromList(["Y","F","N","I","U"]),
			"organDonor":valFromList(["Y","F","I"]),
			"separateBill":YN(),
			"publicity":valFromList(["F","N","U","O"]),
			"protection":YN(),
			"ambulatoryStatus":valFromList(["A0","A1","A2","A3","A4","A5","A6","A7","A8","A9","B1","B2","B3","B4","B5","B6"]),
			"jobStatus":valFromList(["O","P","T","U"]),
			"jobTitle":valFromList(["Teacher","Painter","Software developer","Farmer","CEO","Chef"]),
			"employeeId":randomInt(7),
			"employer":{
				"name":valFromList(["ACME GROUP","VANDELAY INDUSTRIES","THE HUMAN FUND"]),
				"address":{
					"street":street(),
					"state":usState(),
					"zip":zipCode()
				},
				"startDate":randomDate(2015,2016)
			}
	};
	switch (person.sex){
	case "M": person.name.first  = maleName();
	break;
	case "F": person.name.first = femaleName();
	}
	return person;
}
