codeTemplate.code:
/**
	Converts CCD HL7 v2.x date format ISO date format
*/
/* author: abakken */

function convertCCDHL7Date( hl7DateString, timezone ) {
	var isoDate;
	var dateString;
	var hl7Formatter;
	var isoFormatter;

	if (timezone) {
		isoFormatter.setTimeZone(Packages.java.util.TimeZone.getTimeZone(timezone));
	}

	if ( hl7DateString != null && !isNaN(hl7DateString.substring(0,13)) && hl7DateString.length > 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmssZ");
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ");
	}
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 12 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmm");
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 8 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMdd");
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd");
	} 
	else dateString = hl7DateString;

	try {
		if (hl7Formatter) {
			isoDate = hl7Formatter.parse(hl7DateString);
			isoDate = isoFormatter.format(isoDate);
			dateString = new String(isoDate);
		}
	} catch (err) {
		dateString = hl7DateString;
	} finally {
		return dateString;
	}
}

/**
	Get MDHT given name array and turn it into string array
*/

/* author: rwattras */

function mdhtArrayToStringArray(mdhtArray) {
	var array = [];

	for (var q = 0; q < mdhtArray.size(); q++) {
		if (mdhtArray.get(q) != null) {
			array.push(new String(mdhtArray.get(q).getText()));
		}
	}

	return array;
}

/**
	Converts standard HL7 v2.x date format to human-readable ISO date format
*/
/* author: abakken */

function convertHL7Date( date ) {

	/*  Check that the date string is
		1) not null
		2) a positive integer or timestamp
		4) valid length	*/
	var dateString;
	if ( date != null && !isNaN(date.substring(0,13)) && date.length > 14 ) {
		try {
			dateString = new String(DateUtil.convertDate("yyyyMMddHHmmssz", "yyyy-MM-dd'T'HH:mm:ssZ", date));
		} catch (err) {
			dateString = date;
		} finally {
			return dateString;
		}
	} 
	else if ( date != null && !isNaN(date) && date > 0 && date.length == 14 ) {
		try {
			dateString = new String(DateUtil.convertDate("yyyyMMddHHmmss", "yyyy-MM-dd HH:mm:ss", date));
		} catch (err) {
			dateString = date;
		} finally {
			return dateString;
		}
	} 
	else if ( date != null && !isNaN(date) && date > 0 && date.length == 12 ) {
		try {
			dateString = new String(DateUtil.convertDate("yyyyMMddHHmm", "yyyy-MM-dd HH:mm", date));
		} catch (err) {
			dateString = date;
		} finally {
			return dateString;
		}
	} 
	else if ( date != null && !isNaN(date) && date > 0 && date.length == 8 ) {
		try {
			dateString = new String(DateUtil.convertDate("yyyyMMdd", "yyyy-MM-dd", date));
		} catch (err) {
			dateString = date;
		} finally {
			return dateString;
		}
	} 
	else return date;
}

/**
	Converts standard HL7 v2.x date format to human-readable ISO date format
*/

/* author: abakken */

function toISODate( hl7DateString, timezone ) {
	var isoDate;
	var dateString;
	var hl7Formatter;
	var isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ");

	if (timezone) {
		isoFormatter.setTimeZone(Packages.java.util.TimeZone.getTimeZone(timezone));
	}

	if ( hl7DateString != null && !isNaN(hl7DateString.substring(0,13)) && hl7DateString.length > 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmssZ");
	}
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 12 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmm");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 8 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMdd");
	} 
	else dateString = hl7DateString;

	try {
		if (hl7Formatter) {
			isoDate = hl7Formatter.parse(hl7DateString);
			isoDate = isoFormatter.format(isoDate);
			dateString = new String(isoDate);
		}
	} catch (err) {
		dateString = hl7DateString;
	} finally {
		return dateString;
	}
}

/**
	Converts ISO date format to HL7 v2 format
*/
/* author: abakken */

function convertISODate( date ) 
{
	var dateString;
	// yyyy-MM-dd HH:mm:ss
	if ( date != null && date.length == 19
			&& !isNaN(date.substring(0,3)) && date.charAt(4) == "-"
			&& !isNaN(date.substring(5,6)) && date.charAt(7) == "-"
			&& !isNaN(date.substring(8,9)) && date.charAt(10) == " "
			&& !isNaN(date.substring(11,12)) && date.charAt(13) == ":"
			&& !isNaN(date.substring(14,15)) && date.charAt(16) == ":"
			&& !isNaN(date.substring(17,18)) ) {
		try {
			dateString = new String(DateUtil.convertDate("yyyy-MM-dd HH:mm:ss", "yyyyMMddHHmmss", date));
		} catch (err) {
			dateString = date;
		} finally {
			return dateString;
		}
	} 
	// yyyy-MM-dd HH:mm
	else if ( date != null && date.length == 16
			&& !isNaN(date.substring(0,3)) && date.charAt(4) == "-"
			&& !isNaN(date.substring(5,6)) && date.charAt(7) == "-"
			&& !isNaN(date.substring(8,9)) && date.charAt(10) == ' '
			&& !isNaN(date.substring(11,12)) && date.charAt(13) == ":"
			&& !isNaN(date.substring(14,15)) ) {
		try {
			dateString = new String(DateUtil.convertDate("yyyy-MM-dd HH:mm", "yyyyMMddHHmm", date));
		} catch (err) {
			dateString = date;
		} finally {
			return dateString;
		}	
	} 
	// yyyy-MM-dd
	else if ( date != null && date.length == 10
			&& !isNaN(date.substring(0,3)) && date.charAt(4) == "-"
			&& !isNaN(date.substring(5,6)) && date.charAt(7) == "-"
			&& !isNaN(date.substring(8,9)) ) {
		try {
			dateString = new String(DateUtil.convertDate("yyyy-MM-dd", "yyyyMMdd", date));
		} catch (err) {
			dateString = date;
		} finally {
			return dateString;
		}	
	}
	// Timestamp
	else if ( date != null && date.length <= 25 && date.length >= 22 
			&& !isNaN(date.substring(0,3)) && date.charAt(4) == "-"
			&& !isNaN(date.substring(5,6)) && date.charAt(7) == "-"
			&& !isNaN(date.substring(8,9)) && date.charAt(10) == " "
			&& !isNaN(date.substring(11,12)) && date.charAt(13) == ":"
			&& !isNaN(date.substring(14,15)) && date.charAt(16) == ":"
			&& !isNaN(date.substring(17,18)) && date.charAt(19) == " ") {
		try {
			dateString = new String(DateUtil.convertDate("yyyy-MM-dd HH:mm:ss", "yyyyMMddHHmmss", date.substring(0,19)));
		} catch (err) {
			dateString = date;
		} finally {
			return dateString;
		}
	} 
	// ISO-8601
	else if ( date != null && date.length <= 25 && date.length >= 20 
			&& !isNaN(date.substring(0,3)) && date.charAt(4) == "-"
			&& !isNaN(date.substring(5,6)) && date.charAt(7) == "-"
			&& !isNaN(date.substring(8,9)) && date.charAt(10) == "T"
			&& !isNaN(date.substring(11,12)) && date.charAt(13) == ":"
			&& !isNaN(date.substring(14,15)) && date.charAt(16) == ":"
			&& !isNaN(date.substring(17,18)) ) {
		try {
			dateString = new String(DateUtil.convertDate("yyyy-MM-dd'T'HH:mm:ss", "yyyyMMddHHmmss", date.substring(0,19)));
		} catch (err) {
			dateString = date;
		} finally {
			return dateString;
		}
	}
	else return date;
}

/**
	Converts ISO date format to HL7 v2 format
*/

/* author: abakken */

function toHL7Date( isoDateString, timezone ) 
{
	var hl7Date;
	var dateString;
	var isoFormatter;
	var hl7Formatter;

	// ISO Date Format: yyyy-MM-dd'T'HH:mm:ss'Z'
	if ( isoDateString != null && isoDateString.length <= 25 && isoDateString.length >= 22) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmssZ");
		if (timezone) {
			hl7Formatter.setTimeZone(Packages.java.util.TimeZone.getTimeZone(timezone));
		}
	}
	// ISO Date Format: yyyy-MM-dd HH:mm:ss
	else if ( isoDateString != null && isoDateString.length == 19 ) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
	}
	// ISO Date Format: yyyy-MM-dd HH:mm
	else if ( isoDateString != null && isoDateString.length == 16 ) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd HH:mm");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmm");	
	} 
	// ISO Date Format: yyyy-MM-dd
	else if ( isoDateString != null && isoDateString.length == 10 ) {
		isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd");
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMdd");	
	}
	// None of the above, return original date string.
	else dateString = isoDateString;

	try {
		if (isoFormatter) {
			hl7Date = isoFormatter.parse(isoDateString);
			hl7Date = hl7Formatter.format(hl7Date);
			dateString = new String(hl7Date);
		}
	} catch (err) {
		dateString = isoDateString;
	} finally {
		return dateString;
	}
}

/**
	Returns an array of resource objects of specified type.
*/

/* author: abakken */

function getResourceType(type, bundle) {
	var resourceGroup = [];
	if (bundle.entry) {
		for each (item in bundle.entry) 
		{
			if (item.resource.resourceType == type) 
			{
				resourceGroup.push(item.resource);
			}
		}
	}
	return resourceGroup;
}

/**
	Returns the HTTP status code of the response.
*/

function getResponseCode() {
	var httpResult = $('responseStatusLine').split(' ');
	for each (item in httpResult) {
		if (!isNaN(item)) {
			return item;

		}
	}
}

/**
	Returns an array of segments with the specified name that come after a given segment in the message.
*/

/*
	Author: Nick Rupley
	Date Modified: 2/13/2013
	getSegmentsAfter: Returns an array of segments with the specified name that come after a given segment in the message.

	Arguments
	---------
		Required
		--------
			root:			The root HL7Message node of the message, or the parent of the segment node.
			startSeg:		The segment AFTER which to start collecting segments.
			segName:		The name (String or RegExp) of the segments you want to collect.

		Optional
		--------
			consecutiveInd:	If true, indicates that the segments are expected to come directly after startSeg. 
							If false, segments are collected until another segment with the same name as startSeg is encountered.
							Defaults to false.
			stopSegNames:	An array of segment names that, when encountered, stop the collection of segments.
*/

function getSegmentsAfter(root, startSeg, segName, consecutiveInd, stopSegNames) {
	function test(str) {
		return segName instanceof RegExp ? segName.test(str) : segName === str;
	}

	// The index to start collection is the next one up from the starting segment
	var index = startSeg.childIndex()+1;
	// The return array
	var out = [];
	// Boolean placeholder to stop iteration
	var done = false;
	// Object that will contain all of the stopSegNames strings, bound to a truthy value (1)
	var stopNames = {};
	// Indicates whether we have any stop segments
	var stopNamesInd = false;
	// If stopSegNames is defined
	if (stopSegNames !== undefined && stopSegNames !== null) {
		// Set our indicator to true
		stopNamesInd = true;
		// Add each string in the array to our object
		for each (name in stopSegNames)
			stopNames[name] = 1;
	}

	// Iterate through each child in the root, starting at the segment after startSeg, and
	// ending at the final segment, or when the done flag is set to true.
	while (index < root.children().length() && !done) {
		// If a stop segment is encountered, stop iteration
		if (stopNamesInd && root.children()[index].name().toString() in stopNames)
			done = true;
		// If a segment with the same name as startSeg is encountered, stop iteration
		else if (root.children()[index].name().toString() == startSeg.name().toString() && !consecutiveInd)
			done = true;
		// If we're only collecting consecutive segments and we encounter a segment with a name other than segName, stop iteration
		else if (!test(root.children()[index].name().toString()) && consecutiveInd)
			done = true;
		// If all previous tests passed, and the current segment has a name of segName, then add it to our array
		else if (test(root.children()[index].name().toString()))
			out.push(root.children()[index]);
		// Increment our index counter
		index++;
	}

	// Return the output array
	return out;
}

/**
	Converts standard HL7 v2.x date format to human-readable ISO date format
*/

/*
	Author: Datica Inc.
	toISODate: Returns an ISO-formatted date string with specified timezone offset

	Arguments
	---------
		Required
		--------
			hl7DateString:		An HL7-formatted date string (ex: "yyyyMMddHHmmss"

		Optional
		--------
			timezome:			A timezone identifier string corresponding to one of the java.util.TimeZone values (ex: "US/Central")
*/

function toISODate( hl7DateString, timezone ) {
	var isoDate;
	var dateString;
	var hl7Formatter;
	var isoFormatter = new Packages.java.text.SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ssZ");

	if (timezone) {
		isoFormatter.setTimeZone(Packages.java.util.TimeZone.getTimeZone(timezone));
	}

	if ( hl7DateString != null && !isNaN(hl7DateString.substring(0,13)) && hl7DateString.length > 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmssZ");
	}
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 14 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmmss");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 12 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMddHHmm");
	} 
	else if ( hl7DateString != null && !isNaN(hl7DateString) && hl7DateString > 0 && hl7DateString.length == 8 ) {
		hl7Formatter = new Packages.java.text.SimpleDateFormat("yyyyMMdd");
	} 
	else dateString = hl7DateString;

	try {
		if (hl7Formatter) {
			isoDate = hl7Formatter.parse(hl7DateString);
			isoDate = isoFormatter.format(isoDate);
			dateString = new String(isoDate);
		}
	} catch (err) {
		dateString = hl7DateString;
	} finally {
		return dateString;
	}
}

